mapper.select:
SELECT a.id, a.title, a.user_id, a.cover_image, a.qrcode_path, a.is_markdown, a.top, a.type_id, a.`status`, a.recommended, a.original, a.description, a.keywords, a.`comment`, a.`editor_type`, a.`password`, a.`required_auth`, a.create_time, a.update_time, btype.id AS btype_id, btype.`name` AS btype_name, btype.`icon` AS btype_icon, btype.description AS btype_description FROM biz_article a INNER JOIN biz_type btype ON a.type_id = btype.id INNER JOIN biz_article_tags atag ON a.id = atag.article_id WHERE 1 = 1
AND a.type_id = #{typeId}
AND atag.tag_id IN (
#{tagId}
)
AND atag.tag_id = #{tagId}
AND a.top = #{top}
AND a.status = #{status}
AND a.recommended = #{recommended}
AND ( a.description LIKE CONCAT('%',#{keywords , jdbcType=VARCHAR},'%') OR a.keywords LIKE CONCAT('%',#{keywords , jdbcType=VARCHAR},'%') )
AND ( a.title LIKE CONCAT('%',#{title , jdbcType=VARCHAR},'%') )
GROUP BY a.id
ORDER BY RAND()
ORDER BY a.top DESC, a.recommended DESC, a.create_time DESC

SELECT a.id, t.id AS tag_id, t.`name` AS tag_name, t.description AS tag_description FROM biz_article a INNER JOIN biz_article_tags atag ON a.id = atag.article_id INNER JOIN biz_tags t ON atag.tag_id = t.id WHERE a.id IN (
#{id}
)

